package mypackage2;

public class switchdemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 2; 
		switch(x) {
		case 1:
			System.out.println("One");
			break;
		case 2:
			System.out.println("Two");
			break;
		default:
			System.out.println("Please enter 1 or 2");
		}

	}

}
